import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";

export default function MyProducts() {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProducts = async () => {
      const token = localStorage.getItem("token");
      if (!token) return navigate("/login");

      try {
        const res = await fetch("http://localhost:5000/api/my-products", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await res.json();
        if (!res.ok) {
          alert(data.message || "Erro ao buscar produtos");
          return;
        }
        setProducts(data);
      } catch (err) {
        console.error(err);
        alert("Erro de conexão com o servidor");
      }
    };
    fetchProducts();
  }, [navigate]);

  return (
    <div className="min-h-screen bg-gray-100">
      <Header />
      <div className="p-6">
        <h2 className="text-2xl font-bold mb-4">Meus Produtos</h2>
        {products.length === 0 ? (
          <p>Nenhum produto cadastrado ainda.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {products.map((prod) => (
              <div key={prod.id} className="bg-white p-4 rounded shadow">
                <h3 className="font-semibold">{prod.name}</h3>
                <p>R$ {prod.price.toFixed(2)}</p>
                <p>{prod.description}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
