import { useState, useEffect } from "react";
import Header from "../components/Header";

export default function Cart() {
  const [cartItems, setCartItems] = useState([]);
  const [cep, setCep] = useState("");
  const [frete, setFrete] = useState(0);

  // Carregar carrinho do localStorage
  const loadCart = () => {
    const storedCart = localStorage.getItem("cart");
    if (storedCart) setCartItems(JSON.parse(storedCart));
    else setCartItems([]);
  };

  useEffect(() => {
    loadCart();

    const handleCartChange = () => loadCart();
    window.addEventListener("cartChanged", handleCartChange);

    return () => {
      window.removeEventListener("cartChanged", handleCartChange);
    };
  }, []);

  // Ajustar quantidade do item
  const updateQuantity = (index, delta) => {
    const updatedCart = [...cartItems];
    updatedCart[index].quantity = Math.max(1, updatedCart[index].quantity + delta);
    setCartItems(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
    window.dispatchEvent(new Event("cartChanged"));
  };

  // Remover item
  const removeItem = (index) => {
    const updatedCart = [...cartItems];
    updatedCart.splice(index, 1);
    setCartItems(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
    window.dispatchEvent(new Event("cartChanged"));
  };

  // Calcular subtotal
  const subtotal = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);

  // Calcular frete (exemplo fixo ou API via CEP)
  const calcularFrete = () => {
    if (!cep) return alert("Informe o CEP");
    // Aqui você pode chamar uma API real de frete
    const valorFrete = 15.0; // exemplo fixo
    setFrete(valorFrete);
  };

  // Finalizar compra
  const finalizarCompra = () => {
    if (cartItems.length === 0) return alert("Carrinho vazio");
    if (!cep) return alert("Informe o CEP");
    // Salvar subtotal + frete no localStorage ou estado global
    localStorage.setItem("frete", frete);
    // Redirecionar para página de endereço e pagamento
    window.location.href = "/checkout";
  };

  return (
    <div>
      <Header />

      <main className="p-6">
        <h1 className="text-2xl font-bold mb-6">Meu Carrinho</h1>

        {cartItems.length === 0 ? (
          <p>O carrinho está vazio.</p>
        ) : (
          <div className="space-y-4">
            {cartItems.map((item, index) => (
              <div
                key={index}
                className="flex items-center justify-between bg-white p-4 rounded shadow"
              >
                <div className="flex items-center space-x-4">
                  {/* Botões quantidade */}
                  <button
                    onClick={() => updateQuantity(index, -1)}
                    className="bg-gray-300 px-2 py-1 rounded hover:bg-gray-400"
                  >
                    -
                  </button>
                  <span className="w-6 text-center">{item.quantity}</span>
                  <button
                    onClick={() => updateQuantity(index, 1)}
                    className="bg-gray-300 px-2 py-1 rounded hover:bg-gray-400"
                  >
                    +
                  </button>

                  {/* Nome do item */}
                  <span className="ml-4">{item.name}</span>
                </div>

                {/* Valor unitário */}
                <div className="flex items-center space-x-4">
                  <span>R$ {(item.price * item.quantity).toFixed(2)}</span>
                  <button
                    onClick={() => removeItem(index)}
                    className="bg-red-600 text-white px-2 py-1 rounded hover:bg-red-700"
                  >
                    Remover
                  </button>
                </div>
              </div>
            ))}

            {/* Subtotal */}
            <div className="flex justify-end font-bold text-lg">
              Subtotal: R$ {subtotal.toFixed(2)}
            </div>

            {/* Frete */}
            <div className="flex items-center space-x-2 mt-4">
              <input
                type="text"
                placeholder="CEP para calcular frete"
                value={cep}
                onChange={(e) => setCep(e.target.value)}
                className="border p-2 rounded"
              />
              <button
                onClick={calcularFrete}
                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
              >
                Calcular Frete
              </button>
              {frete > 0 && <span>Frete: R$ {frete.toFixed(2)}</span>}
            </div>

            {/* Total */}
            {frete > 0 && (
              <div className="flex justify-end font-bold text-lg mt-2">
                Total: R$ {(subtotal + frete).toFixed(2)}
              </div>
            )}

            {/* Botão finalizar compra */}
            <div className="flex justify-end mt-4">
              <button
                onClick={finalizarCompra}
                className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700"
              >
                Finalizar Compra
              </button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}
