import React, { useState } from 'react';
import Header from '../components/Header';

const CreateProduct = () => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [description, setDescription] = useState('');
  const [stock, setStock] = useState('');
  const [images, setImages] = useState([]);

  const handleSubmit = async e => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', name);
    formData.append('priceCents', parseInt(price*100));
    formData.append('description', description);
    formData.append('stock', parseInt(stock));
    images.forEach(file => formData.append('images', file));

    const res = await fetch('/api/seller/products', {
      method: 'POST',
      body: formData
    });

    if(res.ok) {
      alert('Produto criado!');
      setName('');
      setPrice('');
      setDescription('');
      setStock('');
      setImages([]);
    } else {
      alert('Erro ao criar produto');
    }
  };

  return (
    <>
      <Header />
      <div style={{ padding: '2rem' }}>
        <h1>Criar Produto</h1>
        <form onSubmit={handleSubmit} encType="multipart/form-data" style={formStyle}>
          <input placeholder="Nome" value={name} onChange={e => setName(e.target.value)} required/>
          <input type="number" placeholder="Preço" value={price} onChange={e => setPrice(e.target.value)} required/>
          <input type="number" placeholder="Estoque" value={stock} onChange={e => setStock(e.target.value)} required/>
          <textarea placeholder="Descrição" value={description} onChange={e => setDescription(e.target.value)} required/>
          <input type="file" multiple onChange={e => setImages([...e.target.files])} required/>
          <button type="submit">Criar Produto</button>
        </form>
      </div>
    </>
  );
};

const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '1rem',
  maxWidth: '500px',
  background: '#fff',
  padding: '2rem',
  borderRadius: '10px',
  boxShadow: '0 0 10px rgba(0,0,0,0.1)'
};

export default CreateProduct;
