import React, { useContext } from 'react';
import Header from '../components/Header';
import { CartContext } from '../context/CartContext';

const Cart = () => {
  const { cart, removeFromCart, updateQuantity, total, clearCart } = useContext(CartContext);

  return (
    <>
      <Header />
      <h1>Carrinho</h1>
      {cart.length === 0 ? <p>Carrinho vazio</p> :
        <>
          <ul>
            {cart.map(item => (
              <li key={item.product.id}>
                {item.product.name} - R${(item.product.priceCents/100).toFixed(2)}
                <input type="number" min="1" value={item.quantity} 
                       onChange={e => updateQuantity(item.product.id, parseInt(e.target.value))} />
                <button onClick={()=>removeFromCart(item.product.id)}>Remover</button>
              </li>
            ))}
          </ul>
          <p>Total: R${(total/100).toFixed(2)}</p>
          <button onClick={clearCart}>Limpar Carrinho</button>
          <button>Finalizar Compra</button>
        </>
      }
    </>
  );
};

export default Cart;
