import React, { useState } from "react";
import Header from "../components/Header";
import { useNavigate } from "react-router-dom";
import { useCart } from '../context/CartContext';
import '../components/cart.css';

export default function Cart() {
    const { cartItems, updateItemQuantity, removeItemFromCart, selectedItems, toggleItemSelection, toggleSelectAll, startCheckout } = useCart();
    const [cep, setCep] = useState("");
    const [frete, setFrete] = useState(0);
    const [endereco, setEndereco] = useState(null);
    const navigate = useNavigate();

    const subtotal = cartItems.filter(item => selectedItems.includes(item.id)).reduce((acc, item) => acc + (item.priceCents || item.price * 100) * item.quantity, 0);

    const isAllSelected = cartItems.length > 0 && selectedItems.length === cartItems.length;

    const calcularFrete = async () => {
        if (!cep) return alert("Informe o CEP");
        try {
            const res = await fetch(`https://viacep.com.br/ws/${cep}/json/`);
            const data = await res.json();
            if (data.erro) { alert("CEP invÃ¡lido"); return; }
            setEndereco(data);
            let valorFrete = 0;
            switch (data.uf) {
                case "SP": valorFrete = 1000; break;
                case "RJ": valorFrete = 1200; break;
                case "MG": valorFrete = 1400; break;
                default: valorFrete = 2000;
            }
            setFrete(valorFrete);
        } catch (err) { console.error("Erro ao consultar CEP:", err); alert("Erro ao calcular frete"); }
    };

    const handleProceedToCheckout = () => {
        if (selectedItems.length === 0) {
            alert("Selecione pelo menos um item para continuar.");
            return;
        }
        startCheckout(cartItems.filter(item => selectedItems.includes(item.id)));
        navigate('/checkout');
    };

    const formatPrice = (priceCents) => new Intl.NumberFormat('pt-BR', { style: 'currency', currency: 'BRL' }).format(priceCents / 100);

    return (
        <div>
            <Header />
            <main className="cart-page">
                {cartItems.length === 0 ? (
                    <p>Seu carrinho estÃ¡ vazio.</p>
                ) : (
                    <div className="cart-wrapper">
                        <h1 style={{ fontSize: '2rem', fontWeight: 'bold', marginBottom: '1rem' }}>Meu Carrinho</h1>

                        {/* CORRIGIDO: O cabeÃ§alho agora usa a mesma classe de grid */}
                        <div className="cart-grid cart-header">
                            <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                                <input type="checkbox" checked={isAllSelected} onChange={toggleSelectAll} />
                                <span>Produto</span>
                            </div>
                            <div>PreÃ§o UnitÃ¡rio</div>
                            <div style={{ textAlign: 'center' }}>Quantidade</div>
                            <div>PreÃ§o Total</div>
                            <div style={{ textAlign: 'center' }}>AÃ§Ãµes</div>
                        </div>

                        {cartItems.map((item) => (
                            <div key={item.id} className="cart-item cart-grid">
                                <div className="product-info">
                                    <input type="checkbox" checked={selectedItems.includes(item.id)} onChange={() => toggleItemSelection(item.id)} />
                                    {item.images && item.images.length > 0 ? (
                                        <img src={item.images[0].url} alt={item.name} style={{ width: '80px', height: '80px', objectFit: 'cover', borderRadius: '4px' }} />
                                    ) : (
                                        <div style={{ width: '80px', height: '80px', backgroundColor: '#eee', display: 'flex', alignItems: 'center', justifyContent: 'center', borderRadius: '8px' }}>
                                            <span style={{ fontSize: '12px', color: '#888' }}>Sem Imagem</span>
                                        </div>
                                    )}
                                    <span>{item.name}</span>
                                </div>

                                <span>{formatPrice(item.priceCents || item.price * 100)}</span>

                                <div className="quantity-controls">
                                    <button onClick={() => updateItemQuantity(item.id, -1)} disabled={item.quantity <= 1}>-</button>
                                    <span>{item.quantity}</span>
                                    <button onClick={() => updateItemQuantity(item.id, 1)}>+</button>
                                </div>

                                <span>{formatPrice((item.priceCents || item.price * 100) * item.quantity)}</span>

                                <button onClick={() => removeItemFromCart(item.id)} className="remove-button">
                                    Remover
                                </button>
                            </div>
                        ))}

                        <div className="summary-container" style={{ display: 'flex', flexDirection: 'column', marginTop: '2rem', marginLeft: 'auto' }}>
                            <div className="summary-box" style={{ backgroundColor: '#fff', padding: '1.5rem', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,.05)' }}>
                                <div className="summary-row" style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                                    <span>Subtotal ({selectedItems.length} itens):</span>
                                    <span>{formatPrice(subtotal)}</span>
                                </div>
                                <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'center', marginTop: '1rem' }}>
                                    <input type="text" placeholder="CEP" value={cep} onChange={(e) => setCep(e.target.value)} style={{ border: '1px solid #ced4da', padding: '0.5rem', borderRadius: '4px', width: '100%' }} />
                                    <button onClick={calcularFrete} style={{ backgroundColor: '#0d6efd', color: '#fff', padding: '0.5rem 1rem', borderRadius: '4px' }}>
                                        Calcular Frete
                                    </button>
                                </div>
                                {frete > 0 && (
                                    <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '1rem' }}>
                                        <span>Frete:</span>
                                        <span>{formatPrice(frete)}</span>
                                    </div>
                                )}
                                <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '1.5rem', fontWeight: 'bold', marginTop: '1rem', borderTop: '1px dashed #ced4da', paddingTop: '1rem' }}>
                                    <span>Total:</span>
                                    <span>{formatPrice(subtotal + frete)}</span>
                                </div>
                                <button 
                                    onClick={handleProceedToCheckout} 
                                    disabled={selectedItems.length === 0}
                                    style={{ backgroundColor: '#28a745', color: '#fff', padding: '0.75rem 1.5rem', borderRadius: '9999px', width: '100%', marginTop: '1rem', fontWeight: 'bold', cursor: selectedItems.length === 0 ? 'not-allowed' : 'pointer', opacity: selectedItems.length === 0 ? 0.5 : 1 }}
                                >
                                    Prosseguir para o Checkout
                                </button>
                            </div>
                        </div>
                    </div>
                )}
            </main>
        </div>
    );
}
