import { useState, useEffect } from "react";
import Header from "../components/Header";

export default function Cart() {
  const [cartItems, setCartItems] = useState([]);

  // Carrega itens do carrinho do localStorage
  const loadCart = () => {
    const storedCart = localStorage.getItem("cart");
    if (storedCart) setCartItems(JSON.parse(storedCart));
    else setCartItems([]);
  };

  useEffect(() => {
    loadCart();

    // Atualiza o carrinho caso haja mudanças
    const handleCartChange = () => loadCart();
    window.addEventListener("cartChanged", handleCartChange);

    return () => {
      window.removeEventListener("cartChanged", handleCartChange);
    };
  }, []);

  const handleRemoveItem = (index) => {
    const updatedCart = [...cartItems];
    updatedCart.splice(index, 1);
    setCartItems(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
    window.dispatchEvent(new Event("cartChanged")); // atualiza badge
  };

  return (
    <div>
      {/* Header reaproveitado */}
      <Header />

      <main className="p-6">
        <h1 className="text-2xl font-bold mb-4">Meu Carrinho</h1>

        {cartItems.length === 0 ? (
          <p>O carrinho está vazio.</p>
        ) : (
          <ul className="space-y-4">
            {cartItems.map((item, index) => (
              <li key={index} className="flex justify-between items-center bg-white p-4 rounded shadow">
                <span>{item.name}</span>
                <span>R$ {item.price.toFixed(2)}</span>
                <button
                  onClick={() => handleRemoveItem(index)}
                  className="bg-red-600 text-white px-2 py-1 rounded hover:bg-red-700"
                >
                  Remover
                </button>
              </li>
            ))}
          </ul>
        )}
      </main>
    </div>
  );
}
