// frontend/src/components/ProductCard.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';

// Importe seu CSS de notificação aqui ou certifique-se que ele está em um CSS global

export default function ProductCard({ product }) {
    const { addToCart } = useCart();
    const [showNotification, setShowNotification] = useState(false);

    const handleAddToCart = () => {
        // A função addToCart AGORA RETORNA true se conseguiu adicionar, false caso contrário
        const addedSuccessfully = addToCart(product); 

        if (addedSuccessfully) { // SÓ MOSTRA A NOTIFICAÇÃO SE FOI ADICIONADO COM SUCESSO
            setShowNotification(true);
            setTimeout(() => {
                setShowNotification(false);
            }, 2000);
        }
    };

    const getTransformedUrl = (url) => {
        if (!url) return null;
        const transformation = 'w_200,h_200,c_fill,g_auto'; // c_fill para preencher, g_auto para focar
        const parts = url.split('/upload/');
        return `${parts[0]}/upload/${transformation}/${parts[1]}`;
    };

    const imageUrl = (product.images && product.images.length > 0)
        ? getTransformedUrl(product.images[0].url)
        : null;

    const formattedPrice = new Intl.NumberFormat('pt-BR', {
        style: 'currency',
        currency: 'BRL',
    }).format(product.priceCents / 100 || 0);

    const isOutOfStock = product.stock <= 0;

    return (
        // Remova a tag <style> daqui, coloque o CSS no arquivo ProductCard.css
        <div className="bg-white rounded-lg shadow-md overflow-hidden transform hover:scale-105 transition duration-300 relative product-card-hover-effect">
            <Link to={`/product/${product.id}`} className="block h-48 flex items-center justify-center"> {/* Adicionado flexbox aqui */}
                {imageUrl ? (
                    <img src={imageUrl} alt={product.name} className="max-h-full max-w-full object-contain" />
                ) : (
                    <div className="w-full h-48 bg-gray-200 flex items-center justify-center text-gray-500">
                        <span className="text-sm">Sem Imagem</span>
                    </div>
                )}
            </Link>
            
            <div className="p-4">
                <h3 className="text-lg font-semibold truncate">{product.name}</h3>
                <p className="text-gray-600 mt-1 font-bold">{formattedPrice}</p>
                
                <div className="flex justify-between items-center mt-4">
                    <div>
                        <button
                            onClick={handleAddToCart}
                            disabled={isOutOfStock}
                            className={`text-white text-sm px-3 py-1 rounded transition ${
                                isOutOfStock 
                                    ? 'bg-gray-400 cursor-not-allowed' 
                                    : 'bg-green-600 hover:bg-green-700'
                            }`}
                        >
                            {isOutOfStock ? 'Esgotado' : 'Adicionar ao Carrinho'}
                        </button>
                    </div>
                </div>
            </div>

            <div className={`btn product-card-notification ${showNotification ? 'show' : ''}`}>
                Adicionado! ✔️
            </div>
        </div>
    );
}
