import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";

export default function Sales() {
  const [orders, setOrders] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrders = async () => {
      const token = localStorage.getItem("token");
      if (!token) return navigate("/login");

      try {
        const res = await fetch("http://localhost:5000/api/sales", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await res.json();
        if (!res.ok) {
          alert(data.message || "Erro ao buscar vendas");
          return;
        }
        setOrders(data);
      } catch (err) {
        console.error(err);
        alert("Erro de conexão com o servidor");
      }
    };
    fetchOrders();
  }, [navigate]);

  return (
    <div className="min-h-screen bg-gray-100">
      <Header />
      <div className="p-6">
        <h2 className="text-2xl font-bold mb-4">Minhas Vendas</h2>
        {orders.length === 0 ? (
          <p>Nenhuma venda registrada ainda.</p>
        ) : (
          <table className="min-w-full bg-white shadow rounded">
            <thead>
              <tr className="bg-gray-200">
                <th className="py-2 px-4">Produto</th>
                <th className="py-2 px-4">Cliente</th>
                <th className="py-2 px-4">Quantidade</th>
                <th className="py-2 px-4">Total (R$)</th>
                <th className="py-2 px-4">Data</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order) => (
                <tr key={order.id} className="border-b">
                  <td className="py-2 px-4">{order.productName}</td>
                  <td className="py-2 px-4">{order.clientName}</td>
                  <td className="py-2 px-4">{order.quantity}</td>
                  <td className="py-2 px-4">{order.total.toFixed(2)}</td>
                  <td className="py-2 px-4">{new Date(order.createdAt).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
