import { useEffect, useState } from "react";
import Header from "../components/Header";
import useAuth from "../hooks/useAuth";

export default function Sales() {
  const user = useAuth();
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    if (!user) return;

    const fetchOrders = async () => {
      const token = localStorage.getItem("token");

      try {
        const res = await fetch("http://localhost:4000/api/sales", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const data = await res.json();
        if (!res.ok) {
          alert(data.message || "Erro ao buscar vendas");
          return;
        }

        setOrders(data);
      } catch (err) {
        console.error(err);
        alert("Erro de conexão com o servidor");
      }
    };

    fetchOrders();
  }, [user]);

  return (
    <div className="min-h-screen bg-gray-100">
      <Header />
      <div className="p-6">
        <h2 className="text-2xl font-bold mb-4">Meus Produtos</h2>
        {products.length === 0 ? (
          <p>Nenhum produto cadastrado ainda.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {products.map((prod) => (
              <div key={prod.id} className="bg-white p-4 rounded shadow">
                <h3 className="font-semibold">{prod.name}</h3>
                <p>R$ {prod.price.toFixed(2)}</p>
                <p>{prod.description}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
