import { useEffect, useState } from "react";
import Header from "../components/Header";
import useAuth from "../hooks/useAuth";

export default function MyProducts() {
  const user = useAuth(); // garante que só acessa se tiver login
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) return;

    const fetchProducts = async () => {
      const token = localStorage.getItem("token");

      try {
        const res = await fetch("http://localhost:4000/api/sellers/products", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const data = await res.json();
        if (!res.ok) {
          alert(data.message || "Erro ao buscar produtos");
          return;
        }

        setProducts(data);
      } catch (err) {
        console.error(err);
        alert("Erro de conexão com o servidor");
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, [user]);

  if (!user) return null; // enquanto valida o login, não renderiza nada

  return (
    <div className="min-h-screen bg-gray-100">
      <Header />
      <div className="p-6">
        <h2 className="text-2xl font-bold mb-4">Meus Produtos</h2>

        {loading ? (
          <p>Carregando...</p>
        ) : products.length === 0 ? (
          <p>Você ainda não cadastrou produtos.</p>
        ) : (
          <table className="min-w-full bg-white shadow rounded">
            <thead>
              <tr>
                <th className="border px-4 py-2">Nome</th>
                <th className="border px-4 py-2">Preço</th>
                <th className="border px-4 py-2">Estoque</th>
              </tr>
            </thead>
            <tbody>
              {products.map((p) => (
                <tr key={p.id}>
                  <td className="border px-4 py-2">{p.name}</td>
                  <td className="border px-4 py-2">
                    R$ {p.price.toFixed(2)}
                  </td>
                  <td className="border px-4 py-2">{p.stock}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
