import React, { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard';
import Header from '../components/Header';


const Home = () => {
  const [products, setProducts] = useState([]);
  const mockProducts = [
    { id: 1, name: "Camiseta Azul", priceCents: 5000, images:[{url:"https://www.comerciomix.com.br/media/catalog/product/cache/39699d3c43c18428eb057c8e614b0843/c/a/camiseta-azul-royal-para-sublima_o-tradicional_1.jpg"}], stock:10 },
    { id: 2, name: "Tênis Vermelho", priceCents: 12000, images:[{url:"https://m.media-amazon.com/images/I/81T-XnNAVKL._AC_SY535_.jpg"}], stock:5 },
    { id: 3, name: "Mochila Preta", priceCents: 8000, images:[{url:"https://images.tcdn.com.br/img/img_prod/886231/mochila_para_laptop_preta_seanite_5077_1_f904ef6b83f3d242bf89acafccaab9e3_20231002114959.jpg"}], stock:2 },
  ];
  
  useEffect(() => {
    setProducts(mockProducts);
  }, []);
  

  useEffect(() => {
    fetch('/api/products') // placeholder backend
      .then(res => res.json())
      .then(data => setProducts(data));
  }, []);

  return (
    <>
      <Header />
      <div className="products-grid">
        {products.map(p => <ProductCard key={p.id} product={p} />)}
      </div>
    </>
  );
};

export default Home;
