import React, { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard';
import Header from '../components/Header';

const Home = () => {
  const [products, setProducts] = useState([]);
  const mockProducts = [
    { id: 1, name: "Camiseta Azul", priceCents: 5000, images:[{url:"https://www.google.com/imgres?q=camisa%20azul&imgurl=https%3A%2F%2Fwww.comerciomix.com.br%2Fmedia%2Fcatalog%2Fproduct%2Fcache%2F39699d3c43c18428eb057c8e614b0843%2Fc%2Fa%2Fcamiseta-azul-royal-para-sublima_o-tradicional_1.jpg&imgrefurl=https%3A%2F%2Fwww.comerciomix.com.br%2Fcamiseta-tradicional-azul-royal&docid=YQISDx7uxE2orM&tbnid=nxKHmq_qqO0ejM&vet=12ahUKEwiO26n29MuPAxXLIbkGHfHoF2cQM3oECB4QAA..i&w=850&h=850&hcb=2&ved=2ahUKEwiO26n29MuPAxXLIbkGHfHoF2cQM3oECB4QAA"}], stock:10 },
    { id: 2, name: "Tênis Vermelho", priceCents: 12000, images:[{url:"https://www.google.com/imgres?q=tenis%20vermelho&imgurl=https%3A%2F%2Fm.media-amazon.com%2Fimages%2FI%2F510ooCcCpSL._SY900_.jpg&imgrefurl=https%3A%2F%2Fwww.amazon.com.br%2FKEEZMZ-feminino-caminhada-corrida-Vermelho%2Fdp%2FB08TBVVB53&docid=_0kbMwCWAPXlGM&tbnid=MzZFQv3V6JJiMM&vet=12ahUKEwi-99-F9cuPAxXTIbkGHRPBDXIQM3oECBUQAA..i&w=500&h=500&hcb=2&ved=2ahUKEwi-99-F9cuPAxXTIbkGHRPBDXIQM3oECBUQAA"}], stock:5 },
    { id: 3, name: "Mochila Preta", priceCents: 8000, images:[{url:"https://www.google.com/imgres?q=mochila%20preta&imgurl=https%3A%2F%2Fimages.tcdn.com.br%2Fimg%2Fimg_prod%2F886231%2Fmochila_para_laptop_preta_seanite_5077_1_f904ef6b83f3d242bf89acafccaab9e3_20231002114959.jpg&imgrefurl=https%3A%2F%2Fwww.seanite.com%2Fnotebook%2Fnotebook%2Fmochila%2Fmochila-para-laptop-preta-seanite%3Fsrsltid%3DAfmBOopvH_igj6jJ2ZqTHAcakRkAcS6okkIcqqXDTM9VBQAFL0JRVFzF&docid=WyoCavplwn5gWM&tbnid=KrzdbbzsY5gppM&vet=12ahUKEwiEi66S9cuPAxWWK7kGHfMMD0sQM3oECBkQAA..i&w=1500&h=1500&hcb=2&ved=2ahUKEwiEi66S9cuPAxWWK7kGHfMMD0sQM3oECBkQAA"}], stock:2 },
  ];
  
  useEffect(() => {
    setProducts(mockProducts);
  }, []);
  

  useEffect(() => {
    fetch('/api/products') // placeholder backend
      .then(res => res.json())
      .then(data => setProducts(data));
  }, []);

  return (
    <>
      <Header />
      <div className="products-grid">
        {products.map(p => <ProductCard key={p.id} product={p} />)}
      </div>
    </>
  );
};

export default Home;
