import React from 'react';
import { FaSearch, FaTimes } from 'react-icons/fa';

export default function SearchBar({ query, setQuery }) {
    const handleClearQuery = () => {
        setQuery('');
    };

    return (
        <div className="flex justify-center my-4">
            <div className="flex justify-center max-w-lg">
                <input
                    type="text"
                    className="block w-full rounded-md border border-gray-300 bg-white py-2 pl-3 pr-10 text-sm placeholder-gray-500 focus:border-blue-500 focus:outline-none focus:ring-1 focus:ring-blue-500"
                    placeholder="Pesquisa"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                />
                <div className="absolute inset-y-0 right-0 flex items-center pr-3">
                    {query ? (
                        <button
                            onClick={handleClearQuery}
                            className="text-gray-500 hover:text-gray-700"
                        >
                            <FaTimes className="h-5 w-5" />
                        </button>
                    ) : (
                        <FaSearch className="h-5 w-5 text-gray-500" />
                    )}
                </div>
            </div>
        </div>
    );
}
