import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from "react-router-dom";
import { ShoppingCart, Menu, X } from "lucide-react";
import { useCart } from '../context/CartContext';
import useUser from '../hooks/useUser';
import Logo from './logosemd.png';
import './Header.css';

export default function Header() {
    const user = useUser();
    const navigate = useNavigate();
    const { cartItems } = useCart();
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    // Efeito para impedir o scroll da página quando o menu está aberto
    useEffect(() => {
        if (isMenuOpen) {
            document.body.style.overflow = 'hidden';
        } else {
            document.body.style.overflow = 'auto';
        }
        return () => { document.body.style.overflow = 'auto'; }; // Limpeza
    }, [isMenuOpen]);

    const handleLogout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("user");
        window.dispatchEvent(new Event("userChanged"));
        setIsMenuOpen(false);
        navigate("/login");
    };
    
    const closeMenu = () => setIsMenuOpen(false);

    const totalItems = Array.isArray(cartItems) ? cartItems.reduce((acc, item) => acc + item.quantity, 0) : 0;
    const isLoggedIn = !!user;
    const isSeller = user?.role === "seller";
    const isCustomer = user?.role === "customer" || user?.role === "client";

    return (
        <header className="main-header">
            <div className="header-content">
                <Link to="/" className="logo-link" onClick={closeMenu}>
                    <img src={Logo} alt="Logo da Loja" className="logo-img" />
                </Link>

                {/* Navegação de Desktop (escondida no mobile) */}
                <nav className="desktop-nav">
                    {isLoggedIn ? (
                        <>
                            <span className="user-greeting">Olá, {user.name}</span>
                            {isSeller && (
                                <>
                                    <Link to="/my-products">Meus Produtos</Link>
                                    <Link to="/sales">Minhas Vendas</Link>
                                </>
                            )}
                            {isCustomer && (
                                <button className="cart-button" onClick={() => navigate("/cart")}>
                                    <ShoppingCart />
                                    {totalItems > 0 && <span className="cart-badge">{totalItems}</span>}
                                </button>
                            )}
                            <button onClick={handleLogout} className="logout-button">Sair</button>
                        </>
                    ) : (
                        <>
                            <Link to="/login">Login</Link>
                            <Link to="/register">Cadastro</Link>
                        </>
                    )}
                </nav>

                {/* Botão Hambúrguer (só aparece no mobile) */}
                <button className="hamburger-menu" onClick={() => setIsMenuOpen(!isMenuOpen)} aria-label="Menu">
                    {isMenuOpen ? <X size={32} /> : <Menu size={32} />}
                </button>
            </div>

            {/* Menu Overlay Mobile (fica fora do fluxo principal, controlado pelo estado) */}
            <nav className={`mobile-nav ${isMenuOpen ? 'open' : ''}`}>
                {isLoggedIn ? (
                    <>
                        <span className="user-greeting">Olá, {user.name}</span>
                        {isSeller && (
                            <>
                                <Link to="/my-products" onClick={closeMenu}>Meus Produtos</Link>
                                <Link to="/sales" onClick={closeMenu}>Minhas Vendas</Link>
                                <Link to="/seller-dashboard" onClick={closeMenu}>Painel</Link>
                            </>
                        )}
                        {isCustomer && (
                            <>
                                <Link to="/customer-dashboard" onClick={closeMenu}>Meus Pedidos</Link>
                                <Link to="/cart" onClick={closeMenu}>Carrinho ({totalItems})</Link>
                            </>
                        )}
                        <button onClick={handleLogout} className="logout-button">Sair</button>
                    </>
                ) : (
                    <>
                        <Link to="/login" onClick={closeMenu}>Login</Link>
                        <Link to="/register" onClick={closeMenu}>Cadastro</Link>
                    </>
                )}
            </nav>
        </header>
    );
}
