import { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaShoppingCart } from "react-icons/fa";
import { CartContext } from "../context/CartContext";

export default function Header() {
  const [user, setUser] = useState(null);
  const [cartCount, setCartCount] = useState(0); // quantidade de itens no carrinho
  const { totalItems } = useContext(CartContext);
  const navigate = useNavigate();

  const loadUser = () => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) setUser(JSON.parse(storedUser));
    else setUser(null);
  };

  const loadCart = () => {
    const storedCart = localStorage.getItem("cart");
    if (storedCart) setCartCount(JSON.parse(storedCart).length);
    else setCartCount(0);
  };

  useEffect(() => {
    loadUser();
    loadCart();

    // Eventos customizados para atualizar quando carrinho ou usuário mudarem
    window.addEventListener("userChanged", loadUser);
    window.addEventListener("cartChanged", loadCart);

    return () => {
      window.removeEventListener("userChanged", loadUser);
      window.removeEventListener("cartChanged", loadCart);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  return (
    <header className="bg-white shadow-md p-4 flex justify-between items-center">
      <Link to="/" className="text-2xl font-bold text-blue-600">
        Minha Loja
      </Link>

      <div className="flex items-center space-x-3">
        {user ? (
          <>
            <span className="font-medium text-sm">
              {user.name} ({user.role === "seller" ? "Vendedor" : "Cliente"})
            </span>

            {/* Ícone do carrinho com badge */}
            <Link to="/cart" title="Carrinho" className="relative text-gray-700 hover:text-gray-900">
              <FaShoppingCart size={19} />
              {cartCount > 0 && (
                <span className="absolute -top-2 -right-2 bg-red-600 text-white text-xs w-4 h-4 flex items-center justify-center rounded-full">
                  {cartCount}
                </span>
              )}
            </Link>

            <button
              onClick={handleLogout}
              className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700 text-sm"
            >
              Sair
            </button>
          </>
        ) : (
          <>
            <Link
              to="/login"
              className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
              Login
            </Link>
            <Link
              to="/register"
              className="bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-700"
            >
              Cadastro
            </Link>
          </>
        )}
      </div>
    </header>
  );
}
