// src/pages/Register.jsx
import React, { useState } from 'react';
import Header from '../components/Header';

const Register = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('customer'); // 'customer' ou 'seller'

  const handleRegister = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, email, password, role })
      });

      const data = await res.json();

      if (res.ok) {
        alert('Cadastro realizado com sucesso!');
        setName('');
        setEmail('');
        setPassword('');
        setRole('customer');
      } else {
        alert('Falha no cadastro: ' + data.message);
      }
    } catch (err) {
      console.error(err);
      alert('Erro ao cadastrar!');
    }
  };

  return (
    <>
      <Header />
      <div style={{ maxWidth: '400px', margin: '50px auto', padding: '20px', border: '1px solid #ccc', borderRadius: '10px' }}>
        <h2>Cadastro</h2>
        <form onSubmit={handleRegister}>
          <input
            type="text"
            placeholder="Nome"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
          />
          <input
            type="password"
            placeholder="Senha"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
          />

          <select
            value={role}
            onChange={(e) => setRole(e.target.value)}
            style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
          >
            <option value="customer">Cliente</option>
            <option value="seller">Vendedor</option>
          </select>

          <button type="submit" style={{ width: '100%', padding: '10px', backgroundColor: '#2B0B00', color: '#fff', border: 'none', borderRadius: '5px' }}>
            Cadastrar
          </button>
        </form>
      </div>
    </>
  );
};

export default Register;
