import React, { useState } from "react";
import Header from "../components/Header";
import { useNavigate } from "react-router-dom";
import { useCart } from '../context/CartContext';
import '../components/cart.css'; // Certifique-se que o CSS está sendo importado

export default function Cart() {
    // Pegando todos os dados e funções do nosso context atualizado
    const { 
    cartItems, 
    updateItemQuantity, 
    removeItemFromCart,
    selectedItems,
    toggleItemSelection,
    toggleSelectAll,
    startCheckout // <-- Adicione esta linha
} = useCart();

    const [cep, setCep] = useState("");
    const [frete, setFrete] = useState(0);
    const [endereco, setEndereco] = useState(null);
    const navigate = useNavigate();

    // 1. Criamos uma lista APENAS com os itens que estão selecionados
    const selectedCartItems = cartItems.filter(item => selectedItems.includes(item.id));

    // 2. O subtotal agora é calculado com base na lista de itens selecionados
    const subtotal = selectedCartItems.reduce((acc, item) => acc + (item.priceCents || item.price * 100) * item.quantity, 0);

    const formatPrice = (priceCents) => {
        return new Intl.NumberFormat('pt-BR', {
            style: 'currency',
            currency: 'BRL',
        }).format(priceCents / 100);
    };

    const calcularFrete = async () => {
        if (!cep) return alert("Informe o CEP");
        try {
            const res = await fetch(`https://viacep.com.br/ws/${cep}/json/`);
            const data = await res.json();
            if (data.erro) {
                alert("CEP inválido");
                return;
            }
            setEndereco(data);
            let valorFrete = 0;
            switch (data.uf) {
                case "SP": valorFrete = 1000; break; // Valores em centavos
                case "RJ": valorFrete = 1200; break;
                case "MG": valorFrete = 1400; break;
                default: valorFrete = 2000;
            }
            setFrete(valorFrete);
        } catch (err) {
            console.error("Erro ao consultar CEP:", err);
            alert("Erro ao calcular frete");
        }
    };

    // 3. A finalização da compra valida e envia APENAS os itens selecionados
    const finalizarCompra = () => {
        if (selectedCartItems.length === 0) {
            return alert("Selecione pelo menos um item para prosseguir.");
        }
        if (!cep || !endereco) {
            return alert("Por favor, calcule o frete com um CEP válido.");
        }

        // --- MUDANÇA PRINCIPAL ---
        // Troque a linha do localStorage.setItem por esta:
        startCheckout(selectedCartItems);

        navigate("/checkout");
    };

    // Variável para saber se o checkbox "Selecionar Todos" deve estar marcado
    const isAllSelected = cartItems.length > 0 && selectedItems.length === cartItems.length;

    return (
        <div>
            <Header />
            <main className="cart-page">
                {cartItems.length === 0 ? (
                    <p>Seu carrinho está vazio.</p>
                ) : (
                    // Corrigido: Usando flexbox para alinhar itens e resumo
                    <div className="cart-wrapper" style={{ display: 'flex', flexDirection: 'column', gap: '2rem', maxWidth: '1200px', margin: '2rem auto' }}>
                        {/* Itens do Carrinho - Primeira Coluna */}
                        <div style={{ flexGrow: 1 }}>
                            <h1 style={{ fontSize: '2rem', fontWeight: 'bold', marginBottom: '1rem' }}>Meu Carrinho</h1>

                            {/* Cabeçalho da tabela */}
                            <div className="cart-grid cart-header" style={{ display: 'grid', gridTemplateColumns: '1.5fr 1fr 1fr 1fr', gap: '1rem', alignItems: 'center', padding: '0.5rem 0', borderBottom: '2px solid #dee2e6' }}>
                                <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                                    <input type="checkbox" checked={isAllSelected} onChange={toggleSelectAll} />
                                    <span>Produto</span>
                                </div>
                                <div>Preço Unitário</div>
                                <div style={{ textAlign: 'center' }}>Quantidade</div>
                                <div>Preço Total</div>
                            </div>

                            {/* Lista de itens */}
                            {cartItems.map((item) => (
                                <div key={item.id} className="cart-grid cart-item" style={{ display: 'grid', gridTemplateColumns: '1.5fr 1fr 1fr 1fr', gap: '1rem', alignItems: 'center', padding: '1rem 0', borderBottom: '1px solid #e9ecef' }}>
                                    <div className="product-info" style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                                        <input type="checkbox" checked={selectedItems.includes(item.id)} onChange={() => toggleItemSelection(item.id)} />
                                        {item.images && item.images.length > 0 ? (
                                            <img src={item.images[0].url} alt={item.name} style={{ width: '80px', height: '80px', objectFit: 'cover', borderRadius: '4px' }} />
                                        ) : (
                                            <div style={{ width: '80px', height: '80px', backgroundColor: '#eee', display: 'flex', alignItems: 'center', justifyContent: 'center', borderRadius: '8px' }}>
                                                <span style={{ fontSize: '12px', color: '#888' }}>Sem Imagem</span>
                                            </div>
                                        )}
                                        <span>{item.name}</span>
                                    </div>

                                    <span>{formatPrice(item.priceCents || item.price * 100)}</span>

                                    <div className="quantity-controls" style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '0.5rem' }}>
                                        <button onClick={() => updateItemQuantity(item.id, -1)} disabled={item.quantity <= 1}>-</button>
                                        <span>{item.quantity}</span>
                                        <button onClick={() => updateItemQuantity(item.id, 1)}>+</button>
                                    </div>

                                    <span>{formatPrice((item.priceCents || item.price * 100) * item.quantity)}</span>

                                    <button onClick={() => removeItemFromCart(item.id)} style={{ color: '#dc3545', backgroundColor: 'transparent', border: 'none', cursor: 'pointer' }}>
                                        Remover
                                    </button>
                                </div>
                            ))}
                        </div>

                        {/* Resumo do Pedido - Segunda Coluna */}
                        <div className="summary-container" style={{ width: '400px' }}>
                            <div className="summary-box" style={{ backgroundColor: '#fff', padding: '1.5rem', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,.05)' }}>
                                <h2 style={{ fontSize: '1.5rem', fontWeight: 'bold', marginBottom: '1rem' }}>Resumo</h2>
                                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                                    <span>Subtotal ({selectedItems.length} itens):</span>
                                    <span>{formatPrice(subtotal)}</span>
                                </div>
                                <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'center', marginTop: '1rem' }}>
                                    <input
                                        type="text"
                                        placeholder="CEP"
                                        value={cep}
                                        onChange={(e) => setCep(e.target.value)}
                                        style={{ border: '1px solid #ced4da', padding: '0.5rem', borderRadius: '4px', width: '100%' }}
                                    />
                                    <button onClick={calcularFrete} style={{ backgroundColor: '#0d6efd', color: '#fff', padding: '0.5rem 1rem', borderRadius: '4px' }}>
                                        Calcular Frete
                                    </button>
                                </div>
                                {frete > 0 && (
                                    <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '1rem' }}>
                                        <span>Frete:</span>
                                        <span>{formatPrice(frete)}</span>
                                    </div>
                                )}
                                <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '1.5rem', fontWeight: 'bold', marginTop: '1rem', borderTop: '1px dashed #ced4da', paddingTop: '1rem' }}>
                                    <span>Total:</span>
                                    <span>{formatPrice(subtotal + frete)}</span>
                                </div>
                                <button 
                                    onClick={handleProceedToCheckout} 
                                    disabled={selectedItems.length === 0}
                                    style={{
                                        backgroundColor: '#28a745', color: '#fff', padding: '0.75rem 1.5rem', borderRadius: '9999px', width: '100%', marginTop: '1rem', fontWeight: 'bold', cursor: selectedItems.length === 0 ? 'not-allowed' : 'pointer', opacity: selectedItems.length === 0 ? 0.5 : 1
                                    }}
                                >
                                    Prosseguir para o Checkout
                                </button>
                            </div>
                        </div>
                    </div>
                )}
            </main>
        </div>
    );
}
