import React, { useState, useEffect, useRef } from 'react'; // Importe o useRef
import { Link, useNavigate } from "react-router-dom";
import { ShoppingCart, Menu, X } from "lucide-react";
import { useCart } from '../context/CartContext';
import useUser from '../hooks/useUser';
import useOnClickOutside from '../hooks/useOnClickOutside'; // 1. Importe o novo hook
import Logo from './logosemd.png';
import './Header.css';

export default function Header() {
    const user = useUser();
    const navigate = useNavigate();
    const { cartItems } = useCart();
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    // 2. Crie uma referência para o menu dropdown
    const navRef = useRef();

    // 3. Use o hook para fechar o menu ao clicar fora
    useOnClickOutside(navRef, () => setIsMenuOpen(false));

    const handleLogout = () => { /* ... (sem alterações) ... */ };
    const closeMenu = () => setIsMenuOpen(false);
    const totalItems = Array.isArray(cartItems) ? cartItems.reduce((acc, item) => acc + item.quantity, 0) : 0;
    const isLoggedIn = !!user;
    const isSeller = user?.role === "seller";
    const isCustomer = user?.role === "customer" || user?.role === "client";

    return (
        <header className="main-header">
            <div className="header-content">
                <Link to="/" className="logo-link">
                    <img src={Logo} alt="Logo da Loja" className="logo-img" />
                </Link>

                {/* Navegação de Desktop (escondida no mobile) */}
                <nav className="desktop-nav">
                    {/* ... (código da navegação de desktop sem alterações) ... */}
                </nav>

                {/* Botão Hambúrguer (só aparece no mobile) */}
                <button className="hamburger-menu" onClick={() => setIsMenuOpen(!isMenuOpen)} aria-label="Menu">
                    {isMenuOpen ? <X size={28} /> : <Menu size={28} />}
                </button>
                
                {/* 4. Menu Dropdown Mobile com conteúdo simplificado e ref */}
                <nav className={`mobile-nav ${isMenuOpen ? 'open' : ''}`} ref={navRef}>
                    {isLoggedIn ? (
                        <>
                            <span className="user-greeting">{user.name}</span>
                            
                            {isSeller && (
                                <>
                                    <Link to="/my-products" onClick={closeMenu}>Meus Produtos</Link>
                                    <Link to="/sales" onClick={closeMenu}>Minhas Vendas</Link>
                                    <Link to="/seller-dashboard" onClick={closeMenu}>Painel</Link>
                                </>
                            )}

                            {isCustomer && (
                                <Link to="/cart" className="cart-link" onClick={closeMenu}>
                                    <span>Carrinho</span>
                                    {totalItems > 0 && <span className="cart-badge-mobile">{totalItems}</span>}
                                </Link>
                            )}
                            
                            <button onClick={handleLogout} className="logout-button">Sair</button>
                        </>
                    ) : (
                        <>
                            <Link to="/login" onClick={closeMenu}>Login</Link>
                            <Link to="/register" onClick={closeMenu}>Cadastro</Link>
                        </>
                    )}
                </nav>
            </div>
        </header>
    );
}
